CREATE TABLE `temp`.`tmp_dc_m_firm_lookup` (
  `lookupCode` varchar(45) NOT NULL,
  `displayName` varchar(100) NOT NULL DEFAULT '',
  `value` varchar(100) NOT NULL,
  `isRequired` char(1) DEFAULT NULL,
  `address` varchar(100) DEFAULT NULL,
  `phoneNumber` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`lookupCode`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



update invdb.dc_m_firm_lookup, temp.tmp_dc_m_firm_lookup
set   dc_m_firm_lookup.`address` = tmp_dc_m_firm_lookup.`address`
,  dc_m_firm_lookup.`phoneNumber` = tmp_dc_m_firm_lookup.`phoneNumber`
where dc_m_firm_lookup.`lookupCode` = tmp_dc_m_firm_lookup.`lookupCode`;

update invdb.dc_m_firm_lookup
set address = null
, `phoneNumber` = null
where address = 'test';
