INSERT INTO `wp_posts` VALUES (2391, 2, '2013-09-06 13:16:08', '0000-00-00 00:00:00', '[heading type="h1" margintop="8px" marginbottom="28px" bordered="no" align="center"]Smart investing made simple. [/heading]\n\n[heading type="h1" margintop="28px" marginbottom="28px" bordered="no" align="center"][color]<strong>Our official launch is coming soon</strong>[/color][/heading]\n\n[heading type="h2" margintop="20px" marginbottom="28px" bordered="no" align="left"]We empower individual investors in achieving financial stability by removing the complexity of the markets and reducing the significant costs of investment management through a personalized and disciplined plan.[/heading]\n\n[checklist size="large" align="left"]\n<ul>\n	<li>Get a customized portfolio to meet your investment goals</li>\n	<li>Avoid high advisory and investment fees</li>\n	<li>Rebalance your portfolio automatically on a quarterly basis</li>\n</ul>\n[/checklist]\n\n[separator_full margintop="0px" marginbottom="0px"]\n\n[heading type="h5" margintop="20px" marginbottom="10px" bordered="no" align="center"]Email or follow us through social media for more information.[/heading]', 'Coming Soon', '', 'draft', 'open', 'open', '', '', '', '', '2013-09-06 13:16:08', '0000-00-00 00:00:00', '', 0, 'http://www.invessence.com/?page_id=2391', 0, 'page', '', 0) ; 
INSERT INTO `wp_posts` VALUES (2571, 2, '2013-10-15 12:57:15', '0000-00-00 00:00:00', '[tab id=4]\n[heading type="h2" margintop="8px" marginbottom="28px" bordered="no" align="left"][color]<strong>Socially Responsible Investing</strong>[/color][/heading]\n\n[heading type="h3" margintop="8px" marginbottom="20px" bordered="no" align="left"]Incorporating charitable donations into our business model was one small step that makes a big difference to our investors, and more importantly, to those in need.[/heading]\n\n&nbsp;\n\n<a href="http://www.invessence.com/wp-content/uploads/2013/10/handshake.png"><img alt="handshake" src="http://www.invessence.com/wp-content/uploads/2013/10/handshake.png" width="601" height="398" /></a>\n\n&nbsp;\n\n[checklist size="medium"]\n<ul>\n	<li>Our team supports causes that help save and change children’s lives.</li>\n	<li>We are dedicated to making a contribution to charity-based organizations from our company’s profits.</li>\n	<li>Through your investments with Invessence, your financial planning can also incorporate these giving goals.</li>\n</ul>\n[/checklist]\n[/tab]\n\n[/tabs]', 'Giving Back', '', 'draft', 'open', 'open', '', '', '', '', '2013-10-15 12:57:15', '0000-00-00 00:00:00', '', 0, 'http://www.invessence.com/?page_id=2571', 0, 'page', '', 0) ; 
INSERT INTO `wp_posts` VALUES (2853, 2, '2013-11-21 15:05:05', '0000-00-00 00:00:00', '[well size="large"][heading type="h2" margintop="8px" marginbottom="28px" bordered="no" align="center"]Reach your financial goals with a personalized, disciplined and cost-effective investment plan.[/heading][/well]\n\n&nbsp;\n\n[one_fourth last="no"]\n\n[content_column fontawesomeicon="globe" title="Diversification"]\nOur portfolios are constructed with up to 20 different investments representing 4 core asset classes (Equities, Bonds, Commodities &amp; Cash).\n[/content_column]\n[/one_fourth]\n\n[one_fourth last="no"]\n[content_column fontawesomeicon="money" title="Minimize Cost"]\nThe lower your advisory and investment costs, the greater your share of investment returns.\n[/content_column]\n[/one_fourth]\n\n[one_fourth last="no"]\n[content_column fontawesomeicon="tasks" title="Discipline"]\nMaintaining the optimal portfolio requires time and attention. Invessence offers automatic rebalancing to ensure your success over time and through different market cycles.\n[/content_column]\n[/one_fourth]\n\n[one_fourth last="yes"]\n[content_column fontawesomeicon="signal" title="Long Term"]\nThe market fluctuates over the short-term, focus on investing over the long-term. Our portfolios are built for investors with this strategy in mind.\n[/content_column]\n[/one_fourth]\n\n[heading type="h3" margintop="20px" marginbottom="10px" bordered="no" align="center"]Let us empower you to follow the [color]<a title="Why Invessence?" href="http://www.invessence.com/why-invessence/">Essence of Personal Investing</a> [/color]and achieve your financial goals.[/heading]', 'Invessence Home Page Opt 2 (Large Picture)', '', 'draft', 'open', 'open', '', '', '', '', '2013-11-21 15:05:05', '0000-00-00 00:00:00', '', 0, 'http://www.invessence.com/?page_id=2708', 0, 'page', '', 0) ; 
