INSERT INTO `wp_posts` VALUES (3498, 3, '2013-11-26 14:49:48', '2013-11-26 14:49:48', 'A <b>Registered Investment Adviser</b> is an Investment Adviser registered with the Securities and Exchange Commission or a state\'s securities agency. Individuals or firms that receive compensation for giving advice on investing in securities such as stocks, bonds, mutual funds, or exchange traded funds are generally deemed to be investment advisers.\n\nBecause these financial regulators want all financial advice to be objective, anyone claiming to provide financial planning services must be registered and is held to a Fiduciary Standard for those services.  In order to become registered, the state and SEC demands a certain level of training and credentials for all personnel as well as demanding that the firm adhere to industry “best practices” in all matters.\n\nInvessence is based out of New Jersey and is registered as an investment adviser in several states including Connecticut, New York, New Jersey, and Illinois.', 'What does it mean to be a Registered Investment Adviser?', '', 'inherit', 'open', 'open', '', '2674-autosave-v1', '', '', '2013-11-26 14:49:48', '2013-11-26 14:49:48', '', 2674, 'http://www.invessence.com/?p=3498', 0, 'revision', '', 0) ; 
INSERT INTO `wp_posts` VALUES (3508, 3, '2013-11-11 11:08:29', '2013-11-11 16:08:29', 'A <b>Registered Investment Adviser (RIA)</b> is an Investment Adviser (IA) registered with the Securities and Exchange Commission or a state\'s securities agency. Individuals or firms that receive compensation for giving advice on investing in securities such as stocks, bonds, mutual funds, or exchange traded funds are deemed to be investment advisers.\n\nBecause these financial regulators want all financial advice to be objective, anyone claiming to provide financial planning services must be registered as an RIA and is held to a Fiduciary Standard for those services.  In order to become registered, the state and SEC demands a certain level of training and credentials for all personnel as well as demanding that the firm adhere to industry “best practices” in all matters.\n\nInvessence is based out of New Jersey and is registered as an investment advisor in several states including Connecticut, New York, New Jersey, and Illinois.', 'What does it mean to be a Registered Investment Advisor?', '', 'publish', 'open', 'open', '', 'what-does-it-mean-to-be-a-registered-investment-advisor-2', '', '', '2013-11-11 11:08:29', '2013-11-11 16:08:29', '', 0, 'http://www.invessence.com/?post_type=icompany_faq&amp;p=2674', 2, 'icompany_faq', '', 0) ; 
